export const SecurityScoreIcon = ({ score = 5, ...props }: React.SVGProps<SVGSVGElement> & { score?: number }) => {
  const colors = ["#ff4444", "#ff8844", "#ffaa00", "#88cc44", "#44cc88"];
  const color = colors[Math.min(score - 1, 4)];

  return (
    <svg {...props} viewBox="0 0 60 12" fill="none">
      {Array.from({ length: 5 }).map((_, i) => (
        <path
          key={i}
          transform={`translate(${i * 12}, 0)`}
          d="M6 1l1.545 3.13L11 4.635l-2.5 2.435.59 3.44L6 8.885l-3.09 1.625.59-3.44L1 4.635l3.455-.505L6 1z"
          fill={i < score ? color : "#374151"}
        />
      ))}
    </svg>
  );
};

